import { GetOrderCompletionStatusApi } from './api/get-order-completion-status-api';
import { StartEmbeddedPaymentApi } from './api/start-embedded-payment-api';
/**
 * API functions used by the PaymentClient.
 */
export interface PaymentClientApi {
    /**
     * Starts the embedded payment process and returns the details required to initialise the embedded payment UI.
     */
    startEmbeddedPayment: StartEmbeddedPaymentApi;
    /**
     * Confirms that the order has been completed after the embedded payment is successful.
     */
    getOrderCompletionStatus: GetOrderCompletionStatusApi;
}
