import { OrderCompletionStatus } from './api';
import { PaymentError } from './payment-error';
/**
 * Callback events for the PaymentClient.
 */
export interface PaymentClientEvents {
    /**
     * Callback function that is called when the payment has completed successfully.
     */
    onPaymentSuccessful: (orderCompletionStatus: OrderCompletionStatus) => void;
    /**
     * Callback function that is called when the payment has failed.
     */
    onPaymentFailed: (error: PaymentError) => void;
}
