import { PaymentButtonConfig } from './payment-button-config';
import { PaymentClientApi } from './payment-client-api';
import { PaymentClientEvents } from './payment-client-events';
/**
 * Parameters required to initialise the PaymentClient.
 */
export interface PaymentClientParams {
    /**
     * The ID of the order to be paid.
     */
    orderId: string;
    /**
     * The name of the payment method to be used.
     */
    paymentMethod: string;
    /**
     * The element that the embedded payment UI will be rendered into.
     */
    container: Element;
    /**
     * Configuration options for the payment button.
     * Only relevant if the payment method does not provide a payment button.
     */
    paymentButtonConfig: PaymentButtonConfig;
    /**
     * The CSS class name for the child container.
     * Only relevant if the payment method does not provide a payment button.
     */
    childContainerClassName: string;
    /**
     * Callback events for the PaymentClient.
     */
    events: PaymentClientEvents;
    /**
     * API functions used by the PaymentClient.
     */
    api: PaymentClientApi;
}
