import { SeatSelectionRule } from './seat-selection-rule';
/**
 * Options to customise the behaviour of the the 'cannot leave gap between selected seats' rule
 */
export interface GapBetweenSelectedSeatsRuleOptions {
    /**
     * If true, a gap between selected seats will be allowed if the gap is at least two seats wide. Single seat gaps will still be disallowed.
     * Default: false
     */
    allowGapsWithMultipleSeatsBetweenSelectedSeats: boolean;
}
/**
 * Validates if the selected seats contain a single seat gap
 * between any contiguously selected seats.
 */
export declare const cannotLeaveGapsBetweenSelectedSeatsRule: (customOptions?: Partial<GapBetweenSelectedSeatsRuleOptions> | undefined) => SeatSelectionRule;
