import { SeatSelectionRule } from './seat-selection-rule';
/**
 * Options to customise the behaviour of the the 'single seat gap from an aisle' rule
 */
export interface SingleSeatGapFromAnAisleRuleOptions {
    /**
     * If true, wheelchair seats can be selected regardless of whether they leave a single available seat gap from an aisle.
     * Default: true
     */
    ignoreSelectedWheelchairSeats: boolean;
    /**
     * If true, sofa seats can be selected regardless of whether they leave a single available seat gap from an aisle.
     * Default: true
     */
    ignoreSelectedSofaSeats: boolean;
    /**
     * If true, a seat gap between selected seats and an aisle is allowed if all seats between the gap and the closest unavailable seat in the row are selected.
     * Default: true
     */
    allowWhenAllSeatsBetweenTheSeatGapAndAnUnavailableSeatAreSelected: boolean;
}
/**
 * Creates a SeatSelectionRule that validates if the selected seats contain a single seat gap from an aisle
 * The rule does not fail when the seat gap is the last remaining seat in the contiguous block
 */
export declare const cannotLeaveSingleSeatGapFromAisleRule: (customOptions?: Partial<SingleSeatGapFromAnAisleRuleOptions> | undefined) => SeatSelectionRule;
