import { MxPosition } from './seat-position';
import { MxSeatStatus } from './seat-status';
/**
 * @hidden
 */
export interface MxSeat {
    /**
     * The position of this seat.
     */
    position: MxPosition;
    /**
     * List of [Seat Positions]{@link MxPosition} that make up the group this seat belongs to.
     */
    seatsInGroup: MxPosition[];
    /**
     * The name of the seat. Commonly combined with the row label to give a description of the seat; for example, “A12”.
     */
    seatLabel: string;
    /**
     * The availablity of the seat, can be Empty, Sold or Selected.
     */
    status: MxSeatStatus;
    /**
     * The seats original status, can be Companion, Wheelchair or others (normal seat).
     */
    originalStatus: MxSeatStatus;
}
