import { ExpiryPoints } from './member-points-balance/expiry-points';
import { MemberBalance } from './member-points-balance/member-balance';
/**
 * A customer's loyalty details.
 * @interface LoyaltyMember
 */
export interface LoyaltyMember {
    /** The customer's member Id. */
    memberId: string;
    /** The customer's given name. */
    givenName?: string;
    /** The customer's surname/family name. */
    familyName?: string;
    /** The customer's full name. */
    fullName?: string;
    /** The customer's email address. */
    email?: string;
    /** The customer's mobile phone number. */
    mobilePhone?: string;
    /** The customer's mobile phone number. */
    homePhone?: string;
    /** The customer's date of birth. */
    dateOfBirth?: Date;
    /** The customer's loyalty type */
    memberLevelName?: string;
    /** The customer's card number */
    cardNumber?: string;
    /** The customer's club name */
    clubName?: string;
    /** The customer's loyalty points */
    memberBalance?: MemberBalance;
    /** The customer's expiry loyalty points */
    expiryPoints?: ExpiryPoints;
}
