/**
 * A seat in an order.
 *
 * @export
 * @interface OrderSeat
 */
export interface OrderSeat {
    rowLabel: string;
    seatLabel: string;
    position: OrderSeatPosition;
    areaCategoryCode: string;
}
/**
 * The position of a seat in an order.
 *
 * @export
 * @interface OrderSeatPosition
 */
export interface OrderSeatPosition {
    areaNumber: number;
    rowIndex: number;
    columnIndex: number;
}
