import { Film } from '../browsing/film';
import { TicketType, TicketTypeKey } from '../sales-data/ticket-type';
import { Translation } from '../translation';
import { OrderSeat } from './order-seat';
import { OrderTicket } from './order-ticket';
/**
 * A showtime added to the current order.
 *
 * @export
 * @interface OrderShowtime
 */
export interface OrderShowtime {
    /**
     * ID of the showtime/session.
     */
    showtimeId: string;
    /**
     * The film that's showing for this showtime/session.
     */
    film: Film;
    /**
     * Descriptive name for the screen where the showtime/session is showing.
     */
    screenName?: string;
    /**
     * Translations of the screenName property.
     */
    screenNameTranslations?: Translation[];
    /**
     * Date when the showtime/session is showing.
     */
    startTime: Date;
    /**
     * Is allocated seating available for this showtime.
     */
    isAllocatedSeating: boolean;
    /**
     * Ticket types for this showtime.
     */
    ticketTypes?: Map<TicketTypeKey, TicketType>;
    /**
     * Tickets added to the order.
     */
    tickets: OrderTicket[];
    /**
     * Seats chosen for the order.
     */
    seats: OrderSeat[];
}
