/**
 * A button within an item page.
 */
export interface ItemButton {
    /**
     * The sequeuce in which the button should be displayed within the page.
     */
    displaySequence: number;
    /**
     * The text that should be displayed for the button.
     */
    text?: string;
    /**
     * The id of the Item Model associated with the button.
     */
    itemModelId: string;
}
