import { ItemModifier } from './item-modifier';
/**
 * A group of modifier items along with the requiments to fulfill the group.
 */
export interface ItemModifierGroup {
    /**
     * The id of the modifier group.
     */
    id: string;
    /**
     * The description of the modifier group.
     */
    description: string;
    /**
     * The sequence in which the modifier group should be displayed.
     */
    displaySequence: number;
    /**
     * The short description of the modifier group.
     */
    shortDescription: string;
    /**
     * The maximum quantity (if any) of the modifier group.
     */
    maximumQuantity?: number;
    /**
     * The minimum quantity (if any) of the modifier group.
     */
    minimumQuantity?: number;
    /**
     * The quantity included by default (if any) of the modifier group.
     */
    includedQuantity?: number;
    /**
     * The modifiers that can be selected from for the modifier group.
     */
    modifiers: ItemModifier[];
}
