import { Translation } from './../translation';
import { Row } from './row';
/**
 * A seat area (a block of seats) within a seat layout.
 */
export interface Area {
    /**
     * Uniquely idenfities an area within a seat layout
     */
    number: number;
    /**
     * The code of the area category the area belongs to
     */
    areaCategoryCode: string;
    /**
     * The number of columns within the area
     */
    columnCount: number;
    /**
     * Description of the area
     */
    description: string;
    /**
     * Translations of the description property
     */
    descriptionTranslations: Translation[];
    /**
     * Indicates whether or not the seats in the area can be allocated
     */
    isAllocatedSeating: string;
    /**
     * Indicates whether or not sofa seats are available in the area
     */
    hasSofaSeatingEnabled: string;
    /**
     * The number of rows within the area
     */
    rowCount: number;
    /**
     * The rows within the area
     */
    rows: Row[];
    /**
     * The left position of the area relative to the seat layout
     */
    left: number;
    /**
     * The top position of the area relative to the seat layout
     */
    top: number;
    /**
     * The width of the area relative to the seat layout
     */
    width: number;
    /**
     * The height of the area relative to the seat layout
     */
    height: number;
}
