/**
 * Display text for the Film List widget.
 */
export interface FilmListDisplayText {
    /**
     * The label shown on the 'now showing' tab.
     */
    tabNowShowingLabel: string;
    /**
     * The label shown on the 'coming soon' tab.
     */
    tabComingSoonLabel: string;
    /**
     * Title displayed when film data failed to load from the API.
     */
    failedToLoadDataTitle: string;
    /**
     * Message displayed when film data failed to load from the API.
     */
    failedToLoadDataMessage: string;
    /**
     * Title displayed when there are no films for the selected tab.
     */
    noFilmsForTabTitle: string;
    /**
     * Message displayed when there are no films for the selected tab.
     */
    noFilmsForTabMessage: string;
}
/**
 * Default display text for the Film List widget.
 */
export declare const defaultFilmListDisplayText: FilmListDisplayText;
