/**
 * Display text for the Order Payment Form widget.
 */
export interface OrderPaymentFormDisplayText {
    /**
     * The label shown for the booking fee of the order.
     */
    bookingFeeLabel: string;
    /**
     * The label shown for the total cost of the order.
     */
    totalCostLabel: string;
    /**
     * The label shown for the subtotal cost of the order, excluding any fees.
     */
    subtotalCostLabel: string;
    /**
     * The label shown for the button that starts the payment process.
     */
    paymentButtonLabel: string;
    /**
     * The heading shown for the payment methods.
     */
    paymentMethodsHeading: string;
    /**
     * Title displayed when there is a problem with the payment.
     */
    paymentFailedTitle: string;
    /**
     * Message displayed when there is a problem with the payment.
     */
    paymentFailedMessage: string;
    /**
     * Title displayed when the payment connector fail to initialise.
     */
    failedToInitialiseConnectorTitle: string;
    /**
     * Message displayed when the payment connector fail to initialise
     */
    failedToInitialiseConnectorMessage: string;
}
/**
 * Default display labels for the Order Payment Form widget.
 */
export declare const defaultOrderPaymentFormDisplayText: OrderPaymentFormDisplayText;
