import { OrderCompletionStatus } from '@vista/embedded-payments';
/**
 * Events for the Order Payment Form widget.
 */
export interface OrderPaymentFormEvents {
    /**
     * Callback function that is called when the payment has been completed successfully.
     */
    onPaymentSuccessful: (orderCompletionStatus: OrderCompletionStatus) => void;
    /**
     * Callback function that is called when the payment has failed.
     */
    onPaymentFailed: (error: any) => void;
}
