import { Seat } from '../../../../../types/seating/seat';
import { SeatLayout } from '../../../../../types/seating/seat-layout';
/**
 * Events for the seat picker widget.
 */
export interface SeatPickerWidgetEvents {
    /**
     * Callback function that returns the seat layout data after it has been loaded from the API.
     */
    onSeatLayoutLoaded?: (seatLayout: SeatLayout) => void;
    /**
     * Callback function that is called when the seat layout data could not be loaded from the API.
     */
    onLoadSeatLayoutFailed?: (error: any) => void;
    /**
     * Callback function that returns the seat clicked by the user.
     */
    onSeatClicked?: (seat: Seat) => void;
    /**
     * Callback function that returns selected seats by the user.
     */
    onSelectedSeatsChanged?: (seats: Seat[]) => void;
}
