/**
 * Display text for the Showtime Picker widget.
 */
export interface ShowtimePickerDisplayText {
    /**
     * The time format string for showtimes, excluding token for am/pm designator.
     */
    showtimeTimeFormat: string;
    /**
     * The format string for the showtime's am/pm designator. Leave blank to hide am/pm.
     */
    showtimeAmPmFormat: string;
    /**
     * Label displayed at the front of the list of attributes that all showtimes in a group have in common.
     */
    commonAttributesListLabel: string;
    /**
     * Format string used to display the runtime of a film in minutes.
     * Must include the placeholder '${runtimeMinutes}', which will be populated with the film's runtime expressed in minutes, e.g. "118".
     */
    runtimeFormat: string;
    noSitesSelectedTitle: string;
    noSitesSelectedMessage: string;
    /**
     * Label displayed when there are no showtimes for a site on the selected date.
     */
    noShowtimesForSiteOnDateMessage: string;
    noShowtimesForFilmMessage: string;
    noShowtimesGenericMessage: string;
    actionButtonLabel: string;
    noShowtimesForAnyDateTitle: string;
    noShowtimesForAnyDateMessage: string;
    /**
     * Title displayed when data failed to load from the API.
     */
    failedToLoadDataTitle: string;
    /**
     * Message displayed when data failed to load from the API.
     */
    failedToLoadDataMessage: string;
}
/**
 * Default display text for the Showtime Picker widget.
 */
export declare const defaultShowtimePickerDisplayText: ShowtimePickerDisplayText;
