import { Showtime } from '../../../../../types/browsing/showtime';
/**
 * Events for the Showtime Picker widget.
 */
export interface ShowtimePickerWidgetEvents {
    /**
     * Callback function that is called when a showtime is clicked. By default, selecting a showtime will no have effect.
     */
    onShowtimeClicked?: (showtime: Showtime) => void;
    /**
     * Callback function that is called when the action button is clicked. By default, clicking the action button will no have effect.
     */
    onActionButtonClicked?: () => void;
}
