import { LoyaltyAuthResponse } from '../../../../../types/loyalty/authentication/loyalty-auth';
export interface SignInWidgetEvents {
    /**
     * Callback function that is called before a sign in attempt.
     */
    onBeforeSignIn?: () => void;
    /**
     * Callback function that returns the LoyaltyAuthResponse after a successful sign in attempt.
     */
    onAfterSuccessfulSignIn?: (authResponse: LoyaltyAuthResponse) => void;
    /**
     * Callback function that is called when a sign in attempt fails.
     */
    onAfterFailedSignIn?: () => void;
}
