/**
 * Display text for the Site Picker widget.
 */
export interface SitePickerDisplayText {
    /**
     * Shows the number of unselected sites being displayed and the maximum number of sites that can be selected.
     */
    siteCountLabel: string;
    /**
     * Shows the number of unselected sites being displayed and a message that the maximum number of sites have been selected.
     */
    siteCountLabelMaximumSelected: string;
    /**
     * The placeholder shown in the search input field.
     */
    searchInputPlaceholder: string;
    /**
     * The message shown when there are no sites found for the current search term.
     */
    noSitesFoundForSearchTermMessage: string;
    /**
     * Title displayed when data failed to load from the API.
     */
    failedToLoadDataTitle: string;
    /**
     * Message displayed when data failed to load from the API.
     */
    failedToLoadDataMessage: string;
}
/**
 * Default display text for the Site Picker widget.
 */
export declare const defaultSitePickerDisplayText: SitePickerDisplayText;
