/**
 * Display text for the Ticket Picker widget.
 */
export interface TicketPickerWidgetDisplayText {
    /**
     * The label shown on the Ticket Picker counter.
     */
    counterLabel: string;
    /**
     * The header label for the 'item' column of the Ticket Picker table.
     */
    tableColumnItemHeaderLabel: string;
    /**
     * The header label for the 'price' column of the Ticket Picker table.
     */
    tableColumnPriceHeaderLabel: string;
    /**
     * The header label for the 'quantity' column of the Ticket Picker table.
     */
    tableColumnQuantityHeaderLabel: string;
    /**
     * The header label for the 'subtotal' column of the Ticket Picker table.
     */
    tableColumnSubtotalHeaderLabel: string;
    /**
     * The label for the 'booking fee' footer row of the Ticket Picker table.
     */
    tableFooterRowBookingFeeLabel: string;
    /**
     * The label for the 'order total' footer row of the Ticket Picker table.
     */
    tableFooterRowOrderTotalLabel: string;
    /**
     * The error message shown when too many tickets have been selected in the Ticket Picker widget.
     */
    errorTooManyTicketsSelectedMessage: string;
    /**
     * The error message shown when too few tickets have been selected compared to the number of seats selected in the Ticket Picker widget.
     */
    errorTooFewTicketsSelectedMessage: string;
    /**
     * The error message shown when the selected tickets cannot be purchased for the selected seats in the Ticket Picker widget.
     */
    errorTicketsDoNotMatchAreaCategoriesMessage: string;
    /**
     * The error message shown when no tickets have been selected in the Ticket Picker widget.
     */
    errorNoTicketsSelectedMessage: string;
    /**
     * Title displayed when ticket type data failed to load from the API.
     */
    failedToLoadDataTitle: string;
    /**
     * Message displayed when ticket type data failed to load from the API.
     */
    failedToLoadDataMessage: string;
}
/**
 * Default display text for the Ticket Picker widget.
 */
export declare const defaultTicketPickerDisplayText: TicketPickerWidgetDisplayText;
