import { TicketType } from '../../../../../types/sales-data/ticket-type';
/**
 * Events for the ticket picker widget.
 */
export interface TicketPickerWidgetEvents {
    /**
     * Callback function that returns the ticket type data after it has been loaded from the API.
     */
    onTicketTypesLoaded?: (ticketTypes: TicketType[]) => void;
    /**
     * Callback function that is called when the ticket type data could not be loaded from the API.
     */
    onLoadTicketTypesFailed?: (error: any) => void;
}
